/* eslint-disable no-use-before-define */
/* eslint-disable array-callback-return */
import ChipButton from 'components/common/ChipButton';
import React, { useEffect, useState } from 'react';
import ReactDOM from 'react-dom';
import {
	ScrollMenu,
	getItemsPos,
	slidingWindow,
} from 'react-horizontal-scrolling-menu';
import styled from 'styled-components';
import { HomeChipDummydata } from 'utils/mockData';
import { LeftArrow, RightArrow } from './Arrows';
import { ChipScrollBox } from './ChipscrollBox';

// const elemPrefix = 'test';
// const getId = (index) => `${elemPrefix}${index}`;
// const getItems = () =>
// 	Array(20)
// 		.fill(0)
// 		.map((_, ind) => ({ id: getId(ind) }));

function App() {
	const [items] = useState(HomeChipDummydata);
	const [selected, setSelected] = useState(0);

	const handleItemClick =
		(itemId) =>
		({ getItemById, scrollToItem }) => {
			setSelected(selected !== itemId ? itemId : '');
			scrollToItem(getItemById(itemId), 'smooth', 'center', 'nearest');
		};

	return (
		<ComponentWrapper>
			<ChipContainer>
				<ScrollMenu
					LeftArrow={LeftArrow}
					RightArrow={RightArrow}
					// onWheel={onWheel}
					onMouseUp={({ getItemById, scrollToItem, visibleItems }) =>
						() => {
							// NOTE: for center items
							const { center } = getItemsPos(visibleItems);
							scrollToItem(
								getItemById(center),
								'smooth',
								'center',
							);
						}}
					options={{ throttle: 0 }}
				>
					{items.map((value) => (
						<ChipScrollBox
							title={value.content}
							itemId={value.idx}
							key={value.idx}
							onClick={handleItemClick(value.idx)}
							selected={value.idx === selected}
						/>
					))}
				</ScrollMenu>
				<div className="box-select-chip">
					<button type="button" className="btn-more-info">
						<svg width="16" height="3" viewBox="0 0 16 3">
							<path
								fillRule="evenodd"
								clipRule="evenodd"
								d="M2 0C2.828 0 3.5 0.672 3.5 1.5C3.5 2.328 2.828 3 2 3C1.172 3 0.5 2.328 0.5 1.5C0.5 0.672 1.172 0 2 0ZM8 0C8.828 0 9.5 0.672 9.5 1.5C9.5 2.328 8.828 3 8 3C7.172 3 6.5 2.328 6.5 1.5C6.5 0.672 7.172 0 8 0ZM14 0C14.828 0 15.5 0.672 15.5 1.5C15.5 2.328 14.828 3 14 3C13.172 3 12.5 2.328 12.5 1.5C12.5 0.672 13.172 0 14 0Z"
								fill="currentColor"
							/>
						</svg>
					</button>
				</div>
			</ChipContainer>
			<ModalContainer>
				<div className="box-modalcontent-wrapper">
					{items.map((value) => (
						<ChipButton
							key={value.idx}
							title={value.content}
							itemId={value.idx}
							// onClick={handleItemClick(value.idx)}
							selected={value.idx === selected}
						/>
					))}
				</div>
				<div className="box-moretag-wrapper">
					<button type="button">
						<span>
							관심태그 설정하기
							<span className="btn-endicon-wrapper">
								<span className="SvgIcon_SvgIcon__root__8vwon">
									<svg
										className="SvgIcon_SvgIcon__root__svg__DKYBi"
										viewBox="0 0 18 18"
									>
										<path d="m11.955 9-5.978 5.977a.563.563 0 0 0 .796.796l6.375-6.375a.563.563 0 0 0 0-.796L6.773 2.227a.562.562 0 1 0-.796.796L11.955 9z" />
									</svg>
								</span>
							</span>
						</span>
					</button>
				</div>
			</ModalContainer>
		</ComponentWrapper>
	);
}
export default App;

const ComponentWrapper = styled.div`
	position: relative;
`;

const ModalContainer = styled.div`
	position: absolute;
	top: 58px;
	padding: 30px 30px 13px !important;
	max-width: 1060px;
	border: 1px solid #e1e2e3;
	box-sizing: border-box;
	box-shadow: 0 4px 20px rgb(0 0 0 / 15%);
	border-radius: 4px;
	.box-modalcontent-wrapper {
		padding: 4px;
		flex: 1 1;
		overflow-y: auto;
	}
	.box-moretag-wrapper {
		margin-top: 10px;
		text-align: center;
		button {
			height: 50px;
			font-size: 16px;
			color: #36f;
			padding: 6px 8px;
			position: relative;
			display: inline-flex;
			align-items: center;
			justify-content: center;
			vertical-align: middle;
			min-width: 64px;
			border-radius: 25px;
			font-weight: 700;
			line-height: normal;
			border: none;
			background-color: none;
			cursor: pointer;
		}
	}
	.btn-endicon-wrapper {
		margin-top: -2px;
		margin-right: -1px;
		margin-left: 2px;
	}
`;

const ChipContainer = styled.div`
	margin: 30px 0;
	position: relative;
	margin-right: 60px;
	.box-select-chip {
		position: absolute;
		right: -60px;
		top: 0px;
	}
	.btn-more-info {
		min-width: 50px;
		min-height: 50px;
		display: inline-flex;
		justify-content: center;
		align-items: center;
		border: 1px solid #e1e2e3;
		border-radius: 5px;
		box-shadow: 0 4px 4px rgb(0 0 0 / 5%);
		color: #939393;
	}
`;

function onWheel({ getItemById, items, visibleItems, scrollToItem }, ev) {
	const isThouchpad = Math.abs(ev.deltaX) !== 0 || Math.abs(ev.deltaY) < 15;
	if (isThouchpad) {
		ev.stopPropagation();
		return;
	}
	if (ev.deltaY < 0) {
		const nextGroupItems = slidingWindow(
			items.toItemsKeys(),
			visibleItems,
		).next();
		const { center } = getItemsPos(nextGroupItems);
		scrollToItem(getItemById(center), 'smooth', 'center');
	} else if (ev.deltaY > 0) {
		const prevGroupItems = slidingWindow(
			items.toItemsKeys(),
			visibleItems,
		).prev();
		const { center } = getItemsPos(prevGroupItems);
		scrollToItem(getItemById(center), 'smooth', 'center');
	}
}
